class Rectangle(object):
    """
    Trivial rectangle geometry implementation.
    """

    def __init__(self, width, height):
        self.width = width
        self.height = height

    def area(self):
        return self.width * self.height

    def __str__(self):
        return "Rectangle(%s, %s)" % (self.width, self.height)

    def __repr__(self):
        return self.__str__()
